public void Event()
{
	//订阅事件
	EventManager.ConnectionOpened += new EventHandler<ConnectionEventArg>(EventManager_ConnectionOpened);
	EventManager.BeforeExecute += new EventHandler<CommandEventArg>(EventManager_BeforeExecute);
	EventManager.AfterExecute += new EventHandler<CommandEventArg>(EventManager_AfterExecute);
	EventManager.OnException += new EventHandler<ExceptionEventArg>(EventManager_OnException);

	//数据库操作
	using( ConnectionScope scope = new ConnectionScope() ) {
		Guid guid = "SELECT newid()".AsCPQuery().ExecuteScalar<Guid>();
		try {
			"SELECT x".AsCPQuery().ExecuteScalar<string>();
		}
		catch( Exception ex ) {
			//仅仅为了验证是否能触发OnException函数
		}
	}
}
public void EventManager_OnException(object sender, ExceptionEventArg e)
{
	//当发生异常时,将会触发这个事件
